  const dispatch = useDispatch();
  const [etat, setEtat] = useState(false);
  return (
    <div className="Form">
      <p className="Filter"> Sort by : </p>
      <button className="Filter" onClick={() => dispatch(filter(true))}>Done</button>
      <button className="Filter" onClick={() => dispatch(filter(false))}>ToDo</button>

    </div>
  )
}

export default Filter
