import { getElementError } from '@testing-library/react';
import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap'
import { DragDropContext, Droppable } from 'react-beautiful-dnd';

function Task({ todo, deleteTask, doneTask, editTask }) {
    const [message, setMessage] = useState("");
    const [disabled, setDisabled] = useState(false);


    return (

        <div>
            <Form className='Form'>
                <Form.Group className="mb-3" >
                    <Form.Label style={{ width: '250px' }}>
                        <li>
                            <DragDropContext>
                                <Droppable droppableId="items">
                                <ul className="input"
                                    style={{ textDecoration: todo.etat ? "line-through" : null }}
                                    contentEditable={disabled}
                                    onChange={(e) => setMessage(e.target.value)} >{todo.text}</ul>
                            </Droppable>
                            </DragDropContext>
                        </li>
                    </Form.Label>

                    <Button variant="" onClick={() => doneTask(todo.id)}><img src='./done.jpg' /></Button>

                    <Button variant="" onClick={() => setDisabled(!disabled)}><img src='./edit.png' /></Button>

                    <Button variant="" onClick={() => deleteTask(todo.id)}><img src='./remove.png' /></Button>
                </Form.Group>
            </Form>
        </div>
    )
}

export default Task
