import { useState } from 'react';
import { Form, Button } from 'react-bootstrap'
import { useDispatch , useSelector} from 'react-redux';
import { edit } from '../redux/Actions';

function Task({ todo }) {
    const disabled = useSelector(state => state.disabled);
    const [message, setMessage] = useState("");
    const dispatch = useDispatch()

    return (

        <div>
            <Form className='Form'>
                <Form.Group className="mb-3" >
                    <Form.Label style={{ width: '250px' }}>
                        <li><ul
                            className="input"
                            style={{ textDecoration: todo.etat ? "line-through" : null }}
                            contentEditable={disabled}
                            onChange={e => setMessage(e.target.value)}>{todo.text}

                        </ul>
                        </li>
                    </Form.Label>

                    <Button variant="" ><img src='./done.jpg' /></Button>

                    <Button variant="" onClick={() => dispatch(edit(message))}><img src='./edit.png' /></Button>

                    <Button variant="" ><img src='./remove.png' /></Button>
                </Form.Group>
            </Form>
        </div>
    )
}

export default Task
