import { useState } from 'react';
import { Form, Button } from 'react-bootstrap'
import { useDispatch} from 'react-redux';
import { done, edit, remove } from '../redux/Actions';

function Task({ todo }) {
    const [disabled, setDisabled] = useState(false);
    const [message, setMessage] = useState("");
    const dispatch = useDispatch();

    const  onEdit= (message)=>{ //a refaire with submit et test
        setDisabled (true);
        dispatch(edit({id:todo.id, text:message, etat : todo.etat}));
    }
    return (

        <div>
            <Form className='Form'>
                <Form.Group className="mb-3" >
                    <Form.Label style={{ width: '250px' }}>
                        <li><ul
                            className="input"
                            style={{ textDecoration: todo.etat ? "line-through" : null }}
                            contentEditable={disabled} 
                            onChange={e => setMessage(e.target.value)}>{todo.text}

                        </ul>
                        </li>
                    </Form.Label>

                    <Button variant="" onClick={() => dispatch(done(todo.id))}><img src='./done.jpg' /></Button>

                    <Button variant="" onClick={() => onEdit(message)}><img src='./edit.png' /></Button>

                    <Button variant=""  onClick={() => dispatch(remove(todo.id))} ><img src='./remove.png' /></Button>
                </Form.Group>
            </Form>
        </div>
    )
}

export default Task
