import {Add, Delete,Edit, Done} from './ActionTodo'

export const add = (id, message) => {
    return {
        type:Add,
        payload : {
            id : Math.random(),
            text : message,
            etat : false
        }

}
}

export const remove = id => {
    return {
        type: Delete,
        payload : id,
}
}

export const edit = (id, message) =>{
    return {
        type : Edit,
        payload : {id, message,}
    }
}

export const done = (id)=>{
    return {
        type : Done,
        payload : id 
    }
}
