import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'
import AddTodo from "./component/AddTodo";
import ListTodo from "./component/ListTodo";
import React, { useState } from 'react';
import FlipMove  from "react-flip-move";
import { DragDropContext } from 'react-beautiful-dnd';

function App() {

  const [todos, setTodos] = useState([{
    id: 1,
    text: "Read a book",
    etat: false
  },
  {
    id: 2,
    text: "Go for a walk",
    etat: false
  },
  {
    id: 3,
    text: "Learn portugese",
    etat: false
  }])
  // Add Task
  const addTask = (task)=>{
      if ((task.text != "") && (task.text != " ")) {
        setTodos([...todos,task])
      }
      else alert("Text is required")
  }

  //remove Task
  const deleteTask = (id)=> {
      setTodos(todos.filter(el => el.id!=id));
  }
  //Done Task
  const doneTask =(id) => {
    setTodos(todos.map(todo=>(todo.id===id)?{...todo, etat: !todo.etat}: todo))
  }
  //Edit Task
  const editTask = (text)=>{
    setTodos(todos.map (el =>[...el,text]))
  }
  return (
    <div>
      <FlipMove duration="800" easing='ease-in-out'>
        <AddTodo addTask={addTask}  />
        <ListTodo todos={todos} deleteTask={deleteTask} doneTask={doneTask} editTask={editTask} />
      </FlipMove>
    </div>
  );
}

export default App;
