import { getElementError } from '@testing-library/react';
import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap'

function Task({todo, deleteTask, doneTask}) {
    const [disabled, setDisabled] = useState(false);
    const changeState =() => {
        setDisabled(!disabled)
        if (disabled){
            const input = document.getElementById('input');
            input.style.borderColor = "blue";
            input.disabled="true";
            input.style.border=1;
            input.style.focus.outline = "blue";

        }
      }
    return (

        <div>
            <Form className='Form'>
                <Form.Group className="mb-3" >
                    <Form.Label style={{ width: '250px' }}>
                        <input value={todo.text} id="input" style={{textDecoration: todo.etat ?  "line-through" : null}} />
                    </Form.Label>

                    <Button variant="" onClick={()=>doneTask(todo.id)}><img src='./done.jpg' /></Button>

                    <Button variant="" onClick={()=> changeState()}><img src='./edit.png' /></Button>

                    <Button variant="" onClick={() => deleteTask(todo.id)}><img src='./remove.png' /></Button>
                </Form.Group>
            </Form>
        </div>
    )
}

export default Task
