import { Add, Delete, Done, Edit } from "./ActionTodo";

const initialState ={
    todos : [
        {
          id: 1,
          text: "Read a book",
          etat: false
        },
        {
          id: 2,
          text: "Go for a walk",
          etat: false
        },
        {
          id: 3,
          text: "Learn something new",
          etat: false
        }
      ],
}
function Reducer(state=initialState, action) {
    switch(action.type){
        case Add : 
        return{ 
            ...state,todos:[...state.todos,action.payload]
        };

        case Edit : 
        return{ 
            ...state,//edit à refaure
            todos: state.todos.find(todo=>todo.id === action.payload)
        };
        case Delete :
        return {
            ...state,
            todos : state.todos.filter(todo=> todo.id !== action.payload)
        }
        case Done : 
        return{
          ...state,
          todos :  state.todos.map(el=>el.id===action.payload ? {...el,done :!el.done}: el)
        }
        default :
        return(state)
    }
}

export default Reducer;
