import { getElementError } from '@testing-library/react';
import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap'

function Task({ todo, deleteTask, doneTask, editTask}) {
    const [message, setMessage] = useState("");
    const [disabled, setDisabled] = useState(true);


    return (

        <div>
            <Form className='Form'>
                <Form.Group className="mb-3" >
                    <Form.Label style={{ width: '250px' }}>
                        <input value={todo.text} className="input"
                            style={{ textDecoration: todo.etat ? "line-through" : null }}
                            disabled={disabled}
                            type="text"
                            onChange={(e) => setMessage(e.target.value)} />
                    </Form.Label>

                    <Button variant="" onClick={() => doneTask(todo.id)}><img src='./done.jpg' /></Button>

                    <Button variant="" onClick={() => setDisabled(!disabled)}><img src='./edit.png' /></Button>

                    <Button variant="" onClick={() => deleteTask(todo.id)}><img src='./remove.png' /></Button>
                </Form.Group>
            </Form>
        </div>
    )
}

export default Task
